// CustomerTable: displays list, provides client-side pagination and sorting.
import React, { useState, useMemo } from 'react';

export default function CustomerTable({ customers, onRowClick }) {
  // pagination state
  const [page, setPage] = useState(1);
  const pageSize = 10;

  // sorting state: {key, dir} where dir is 'asc' or 'desc'
  const [sort, setSort] = useState({ key: 'name', dir: 'asc' });

  // sort handler toggles direction when same column clicked
  function handleSort(key) {
    setSort(prev => ({ key, dir: prev.key === key && prev.dir === 'asc' ? 'desc' : 'asc' }));
  }

  // sorted and paginated data memoized
  const paged = useMemo(() => {
    const arr = [...(customers || [])];
    arr.sort((a, b) => {
      const aVal = (a[sort.key] || '') + '';
      const bVal = (b[sort.key] || '') + '';
      if (aVal < bVal) return sort.dir === 'asc' ? -1 : 1;
      if (aVal > bVal) return sort.dir === 'asc' ? 1 : -1;
      return 0;
    });
    const start = (page - 1) * pageSize;
    return arr.slice(start, start + pageSize);
  }, [customers, page, sort]);

  const totalPages = Math.max(1, Math.ceil((customers || []).length / pageSize));

  return (
    <div className="table-container">
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort('name')}>Name</th>
            <th onClick={() => handleSort('dob')}>Date of Birth</th>
            <th onClick={() => handleSort('nic')}>NIC</th>
            <th>Mobiles</th>
            <th>Families</th>
          </tr>
        </thead>
        <tbody>
          {paged.map(c => (
            <tr key={c.id} onClick={() => onRowClick(c)}>
              <td>{c.name}</td>
              <td>{c.dob || ''}</td>
              <td>{c.nic || ''}</td>
              <td>{(c.mobiles || []).length}</td>
              <td>{(c.families || []).length}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="pagination">
        <button onClick={() => setPage(1)} disabled={page === 1}>First</button>
        <button onClick={() => setPage(p => Math.max(1, p - 1))} disabled={page === 1}>Prev</button>
        <span>Page {page} / {totalPages}</span>
        <button onClick={() => setPage(p => Math.min(totalPages, p + 1))} disabled={page === totalPages}>Next</button>
        <button onClick={() => setPage(totalPages)} disabled={page === totalPages}>Last</button>
      </div>
    </div>
  );
}
